import React from 'react'
import { storiesOf } from '@storybook/react'

import getUserAgent from './getUserAgent'

// REMAINING
// import animate from './animate.md'
// import debounce from './debounce.md'
// import idify from './idify.md'
// import removeKnownProps from './removeKnownProps.md'
// import smoothScroll from './smooth-scroll.md'

const storyArray = [
  'agnostify',
  'bem',
  'canUseDom',
  'findabilityUrlHelper',
  'formatPartNumber',
  'formatVariants',
  'isEmpty',
  'randomString',
  'sortVariants',
]

const getStory = (markdown) => <div className='markdown-style' dangerouslySetInnerHTML={{ __html: markdown }} />
const stories = storiesOf('Utils|Helper Functions', module)

stories.add('Argos-Utils Setup', () => getStory(require(`../README.md`)))

for (let i = 0; i < storyArray.length; i++) {
  const story = storyArray[i]
  const markdown = require(`./${story}.md`)
  stories.add(story, () => getStory(markdown))
}

stories.add('getUserAgent', () => (
  <div>
    <div
      style={{
        margin: 30,
        padding: 15,
        background: 'whitesmoke',
        maxWidth: 250,
        borderRadius: 3,
        border: '1px solid lightgrey',
      }}>
      <div
        className='font-standard'
        style={{
          borderBottom: '1px solid lightgrey',
          paddingBottom: 10,
          marginBottom: 10,
        }}>
        Your Current Browser is
        <span className={`font-bold text-color-${getUserAgent().supported ? 'green' : 'red'}`}>
          {getUserAgent().supported ? ' supported!' : ' NOT supported!'}
        </span>
      </div>
      <div className='font-bold'>
        {getUserAgent().name} - v{getUserAgent().version}
      </div>
    </div>
    {getStory(require(`./getUserAgent.md`))}
  </div>
))
